import React, { Component } from "react";


class Price extends Component {
constructor(props){
        super(props);
        
        this.state = {price: props.price};
    }
    

buttonClick = () =>{
    this.setState({price: this.state.price})
} 


    render(){
        return(
            <div>
                <p>
                {this.state.price}
                </p>
                <button onClick={this.buttonClick} >
                Click
                </button>
            </div>    
        );
    };
}


export default Price;
