import React, { Component } from "react";


class Price extends Component {
    state = {
        price : 100
    };

buttonClick = () => this.state.price= this.state.price * 4.8;

    

    render(){
        return(
            <div>
                <p>
                {this.state.price}
                </p>
                <button
                onClick = {this.buttonClick}
                >Click</button>
            </div>
            
        );
    };
}

export default Price;
