import React, { Component } from "react";


class Price extends Component {
    state = {price: 100};

buttonClick = () =>{
    this.setState({price: this.state.price * 4.8})
} 

buttonTogle = ()=>{
    this.setState({price: !this.state.price * 4.8})
}

    render()
    {
        return(
            <div>
                <p>
                {this.state.price}
                </p>
                <button
                onClick = {this.buttonClick,this.buttonClick}>
                Click
                </button>
            </div>    
        );
    };
}


export default Price;
