import React from 'react';
import styles from '../Task/task.module.css';

function Task(props) {
    let clases = [styles.page];
    if(props.selected){
        clases.push(styles.selected)
    }
    return (
       

        
          <li className={clases.join(' ')}>
              {props.text}
          </li>

      



    );

}

export default Task;
