import React, { Component } from "react";


class Price extends Component {
    state = {price : 100};

    

buttonClick = () =>{
    this.setState({
        price: this.state.price *4.8
       
    })
} 
toggleClick = () =>{
    this.setState({
        price: this.state.price
    })
}

    render(){
        return(
            <div>
                <p>
                {this.state.price(this.toggleClick)}
                </p>
                <button
                onClick = {this.buttonClick}>
                Click
                </button>
            </div>    
        );
    };
}


export default Price;
