import React, {Component} from 'react';

class ToDo extends Component{
    state = {
        inputValue: ''
    }

    handleClick = () =>{
        this.setState({
            inputValue: this.state.inputValue
        })
    }

    render(){
        return(
            <div>
                <input 
                type='text'
                 placeholder='Add Task'>
                     {this.state}
                </input>
                <button
                onClick={this.handleClick}
                >
                Add
                </button>
            </div>
        );
    };
};

export default ToDo;
