import React, { Component } from "react";


class Price extends Component {
    constructor(props){
        super(props),
        state = {price: 100}
    }
    

buttonClick = () =>{
    
    this.setState({price: this.state.price})
} 

buttonDownClick = ()=>{
    this.setState({price: this.state.price * 4.8})
}

    render(){
        return(
            <div>
                <p>
                {this.state.price}
                </p>
                <button onClick={this.buttonClick} >
                Click
                </button>
            </div>    
        );
    };
}


export default Price;
