import React, { Component } from 'react';
import { FormControl, InputGroup, Button, Container, Row, Col, Card } from 'react-bootstrap'
import Task from '../Task/Task';
import styles from './toDo.module.css';
import idGenerator from '../../Support/idGenerator'



class ToDo extends Component {
    state = {
        inputValue: '',
        task: []
    };

    handleChange = (event) => {
        this.setState({
            inputValue: event.target.value
        })
    }

    handleClick = (event) => {
        let { inputValue } = this.state;
        let task = [...this.state.task]
        task.push(inputValue);
        this.setState({
            task: task,
            inputValue: ''
        })
    }
    render() {

        let { inputValue } = this.state;
        let { task } = this.state;


        let tasks = task.map((task) => {
            return <Task text={task} key={idGenerator()} />
        })
let card = [];
for(let i = 1; i <=20; i++){
    card.push(i)
}
  
     
        return (
            <>
                <div className={styles.toDo}>
                    < Container>
                        <Row className='justify-content-center'>
                            <Col sm={8} xs={6} md={12} es={4}>
                                <InputGroup>
                                    <FormControl
                                        placeholder="Add Task"
                                        aria-label="Recipient's username"
                                        aria-describedby="basic-addon2"
                                        value={inputValue}
                                        onChange={this.handleChange}
                                    />
                                    <InputGroup.Append>
                                        <Button variant="outline-primary"
                                            onClick={this.handleClick}>Add</Button>
                                    </InputGroup.Append>
                                </InputGroup>
                            </Col>
                        </Row>

                        <Row >
                            <Col>
                                <Card style={{ width: '18rem' }}>
                                    <Card.Body>
                                        <Card.Title>Card Title</Card.Title>
                                        <Card.Text>
                                         {tasks}
                                         </Card.Text>
                                        <Button variant="primary">Go somewhere</Button>
                                    </Card.Body>
                                </Card>
                            </Col>
                        </Row>

                    </Container>
                </div>
            </>
        );
    };
};

export default ToDo;
