import React, { Component } from "react";


class Price extends Component {
    state = {price: 100};

buttonClick = () =>{
    let changeValue = this.state.price * 4.8
    this.setState({price: changeValue })
    this.setState(()=>{
        if(this.state.price === changeValue){
             {price:this.state.price}
        }
    })
} 
    render()
    {
        return(
            <div>
                <p>
                {this.state.price}
                </p>
                <button
                onClick = {this.buttonClick}>
                Click
                </button>
            </div>    
        );
    };
}


export default Price;
